    public partial class frmElegirUbicacion : Form
    {
        (double Latitude, double Longitude, string LocationName) myLocation;
        GMarkerGoogle currentPosition = new GMarkerGoogle(new PointLatLng(-12.066068464913114, -77.07968681120519), GMarkerGoogleType.red_dot);
        GMapOverlay markers = new GMapOverlay("markers");
        public (double, double, string) MyLocation { get => myLocation; set => myLocation = value; }

        public frmElegirUbicacion()
        {
            InitializeComponent();
            double latitude = -12.07003;
            double longitude = -77.079460;
            mcMap.DragButton = MouseButtons.Left;
            mcMap.MouseWheelZoomEnabled = true;
            mcMap.CanDragMap = true;
            mcMap.MapProvider = GoogleMapProvider.Instance;
            mcMap.Position = new GMap.NET.PointLatLng(latitude, longitude);
            mcMap.MinZoom = 16;
            mcMap.MaxZoom = 20;
            mcMap.Zoom = 18;
            mcMap.ShowCenter = true;
            addMarkers();
        }
        private void mcMap_MouseClick(object sender, MouseEventArgs e)
        {
            if (e.Button == MouseButtons.Left)
            {
                markers.Markers.Remove(currentPosition);
                var point = mcMap.FromLocalToLatLng(e.X, e.Y);
                var resources = new ResourceManager(typeof(frmElegirUbicacion));
                var redpoint = new Bitmap((System.Drawing.Image)resources.GetObject("pointred"));
                currentPosition = new GMarkerGoogle(new PointLatLng(point.Lat, point.Lng), redpoint);
                tbLatitude.Text = point.Lat.ToString();
                tbLongitude.Text = point.Lng.ToString();
                tbName.Text = "";
                mcMap.Position = point;
                markers.Markers.Add(currentPosition);
            }
        }

        private void mcMap_OnMarkerClick(GMapMarker item, MouseEventArgs e)
        {
            markers.Markers.Remove(currentPosition);
            tbLatitude.Text = item.Position.Lat.ToString();
            tbLongitude.Text = item.Position.Lng.ToString();
            tbName.Text = item.ToolTipText;
        }

        private void btnChooseLocation_Click(object sender, EventArgs e)
        {
            if (tbName.Text == "" && tbLatitude.Text != "" && tbLongitude.Text != "")
            {
                tbName.Text = "Nombre desconocido";
            }
            if (tbName.Text == "" || tbLatitude.Text == "" || tbLongitude.Text == "")
            {
                frmMessageBoxAceptar _frmmba = new frmMessageBoxAceptar("", "Debe elegir una ubicación");
                _frmmba.ShowDialog();
            }
            else
            {
                myLocation = (Convert.ToDouble(tbLatitude.Text), Convert.ToDouble(tbLongitude.Text), tbName.Text);
                this.DialogResult = DialogResult.OK;
            }
        }

