	select p.id_receta,p.nombre,p.descripcion,p.indicaciones,p.tipo,p.estado from receta p
	INNER JOIN receta_has_presetreceta i ON i.fid_receta=p.id_receta  where i.fid_presetReceta=_id_Preset_Receta;

