	IN _fid_usuario INT
)
BEGIN
	SELECT fecha, horas 
    FROM horassueno
    WHERE estado = 1
    AND fid_alumno = _fid_usuario
	ORDER BY fecha DESC
	LIMIT 8;
END$$

