                IN _id_usuario INT,
                IN _codigoPUCP VARCHAR(8),
                IN _contrasena VARCHAR(256),
                IN _nombres VARCHAR(40),
                IN _apellidos VARCHAR(40),
                IN _emailPUCP VARCHAR(256),                             
                IN _emailPersonal VARCHAR(256),    
                IN _telefonoMovil VARCHAR(9),                 
